mapper.select:
select product_no
from product p
where product_no = #{product_no,jdbcType=VARCHAR}
and shop_id = #{shop_id,jdbcType=VARCHAR}

mapper.update:
update product
set product_price = #{product_price,jdbcType=VARCHAR},
product_point = #{product_point,jdbcType=VARCHAR}
where product_no = #{product_no,jdbcType=VARCHAR}
and shop_id = #{shop_id,jdbcType=VARCHAR}
